.order{

    background: lightgray;
    width: 175%;
    height: 32px;
    text-align: center;
    font-size: 20px;
    font-weight: 900;
    border:2px solid black;
    border-radius:1px;

}
header .row .header-rt .order span {
  color: #000;
  font-size: large;
  font-weight: bold;
  background-color: #c0c0c0;
  border: 1px solid #000;
  padding: 4px 100px;
}

header img{
    width: 110px;
}

#welcome-table .row {
    display: flex;
  align-items: end;
  width: 100%;
}

#welcome-table .col-5{
    width: 42%;
}

#welcome-table .col-7{
    width: 53%;
}

.row{
    width: 100%;
}

.right,
.left {
  width: 100%;
  border: 3.5px solid #000;
  border-radius: 20px;
  padding: 10px ;
}

.right fieldset {
  width: 300px;
  left: 0 !important;
}

