}

.welcome-table {
  position: relative;
  margin-bottom: 0;
}

.l-td{
}

fieldset {
       position: relative;
    top: -26px;
  left: 20px;
        z-index: 99;
  width: 80%;
  background-color: #fff;

}
fieldset legend {
  margin: 0;
  padding: 0;
  border: 1px solid #000;
  font-size: 14px;
  padding: 0 6px;
  font-weight: bold;
}

.d-flex {
    display: flex;

    justify-content: space-between;
}

.invoice-data {
  border: 1px solid #000;
  border-radius: 15px;
  padding: 7px !important;
  width: 100%;
}
.invoice-data tbody tr {
  width: 100%;
}
.invoice-data tbody tr td:first-child {
  width: 15%;
}
.invoice-data tbody tr .sec-td {
  width: 45%;
}

#main-table {
  border: none !important;
  margin-left: 0px;
}
#main-table thead tr {
  width: 100%;
}
#main-table thead tr th {
    color: #000;
  padding: 2px 10px;
  text-align: center;
  text-transform: uppercase;
  border: 1px solid #000 !important;
  font-weight: bold;
}
#main-table thead tr th:first-child {
  font-size: 15px;
}
#main-table tbody tr td {
  border: 1px solid #000 !important;
}

.total{

    text-align-last: end;
    padding-right: 2%;
    font-size: 14px;
    margin-top: -15px;
    margin-bottom:5px;
    font-weight: 600;
}

.line{
     width:35%;
     margin-bottom:2px;
     border:1px solid black;
     margin-left: 65%;
}



.f-th {
  width: 20%;
