css:
.print-format td, .print-format th{
    padding: 0 !important;
}

.table {
  margin: 0 !important;
}

p,
ol {
  margin: 0 !important;
  padding: 0 !important;
}

.uppercase {
  text-transform: uppercase;
}

td {
  border: 0 !important;
  padding: 5px 4px !important;
}

.bold {
  font-weight: bold;
}

header .row {
  display: flex;
  align-items: center;
  width: 100%;
}
header .row .header-lt {
  width: 25%;
}
header .row .header-rt {
  width: 70%;
  text-align: center;
}
header .row .header-rt .order {
  margin-top: 10px;
}
header .row .header-rt .order span {
  color: #000;
  font-size: large;
  font-weight: bold;
  background-color: #c0c0c0;
  border: 1px solid #000;
  padding: 4px 100px;
}

header img{
    width: 110px;
}

#welcome-table .row {
    display: flex;
  align-items: end;
  width: 100%;
}

#welcome-table .row .col-5{
    width: 45%;
}

#welcome-table .row .col-7{
    width: 55%;
}

.row{
    width: 100%;
}

.right,
.left {
  width: 48%;
  border: 5px solid #000;
  border-radius: 20px;
  padding: 10px ;
}

.right {
  float: right;
  margin-left: auto;
}
.right fieldset {
  width: 300px;
  left: 0 !important;
}

.l-td-auth{

}

.welcome-table {
  position: relative;
  margin-bottom: 0;
}

.l-td{
}

fieldset {
        position: relative;
    top: -26px;
  left: 20px;
        z-index: 99;
  width: 180px;
  background-color: #fff;

}
fieldset legend {
  margin: 0;
  padding: 0;
  border: 1px solid #000;
  font-size: 14px;
  padding: 0 6px;
  font-weight: bold;
}

.invoice-data {
  border: 3px solid #000;
  border-radius: 30px;
  padding: 10px !important;
}
.invoice-data tbody tr {
  width: 100%;
}
.invoice-data tbody tr td:first-child {
  width: 15%;
}
.invoice-data tbody tr .sec-td {
  width: 45%;
}

#main-table {
  border: none !important;
}
#main-table thead tr {
  width: 100%;
}
#main-table thead tr th {
    color: #000;
  padding: 2px 10px;
  text-align: center;
  text-transform: uppercase;
  border: 1px solid #000 !important;
  font-weight: bold;
}
#main-table thead tr th:first-child {
  font-size: 15px;
}
#main-table tbody tr td {
  border: 1px solid #000 !important;
}
#main-table tbody tr:last-child {
  position: relative;
}
#main-table tbody tr:last-child td {
  border: none !important;
  border-bottom: 2px solid #000 !important;
  position: absolute;
  right: 0;
  top: 10px;
  width: 26%;
}
#main-table tbody #total-amount td {
  border: none !important;
}
#main-table tbody #total-amount td:first-child {
  text-align: end;
  padding-right: 20px !important;
}

.f-th {
  width: 15%;
}

.f-th-tv {
  width: 15%;
}

.f-th-tw {
  width: 40%;
}

.f-th-noq {
  width: 7%;
}

.parti-num {
  font-size: 12px !important;
  padding: 0 !important;
}

.desc {
  padding: 0 15px !important;
  font-size: 15px;
}
.container{
    padding:0px!important;
}
.container ol {
  margin-left: 15px !important;
}

